import React, {useContext, useEffect, useState} from 'react'
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import {Grid} from "@material-ui/core";
import FiltersComponent from "../Components/FiltersComponent";
import ProductListComponent from "../Components/ProductListComponent";
import {FiltersContext} from "../FiltersContext";

const HomePage = () => {
    const [ loading, setLoading ] = useState(false)
    const [ data, setData ] = useState()
    const { filtersObj, updateFilters } = useContext(FiltersContext)

    useEffect(() => fetchData(), []);

    function setFiltersObject(data) {
        let filtersObject = []
        if (data !== undefined) {
            Object.keys(data).forEach((key, a) => {
                console.log(key)
                let object = {
                    fieldName: key,
                    options: data[key],
                    selectedOption: "",
                    changeOption: changeFilterOption
                }
                filtersObject.push(object)
            })
        }

        updateFilters(filtersObject)
    }

    const changeFilterOption = (currentI, newOption = "AAA") => {
        if (filtersObj !== undefined && filtersObj[currentI] !== undefined) {
            filtersObj[currentI].selectedOption = newOption
            updateFilters(filtersObj)
        }
    }

    const fetchData = () => {
        setLoading(true)

        fetch("https://www.homedepot.ca/api/search/v1/search?category=l4-buffets-sideboards&store=7008&filter=&pageSize=40&lang=en")
            .then(response => response.json())
            .then(data => {
                setData(data['products'])
                setFiltersObject(data['products'])
            })
            // .then(() => setLoading(false))
    }

    return <div style={{marginTop: '30px'}}>
        <CssBaseline />
        <Container maxWidth="false">
            <Grid container spacing={3}>
                <Grid item xs={4}>
                    <FiltersComponent filtersObj={filtersObj} />
                </Grid>
                <Grid item xs={8}>
                    <ProductListComponent loading={loading} products={data} />
                </Grid>
            </Grid>
        </Container>
    </div>
}

export default HomePage
