import React, {useState} from "react"
const FiltersContext = React.createContext()

function FiltersContextProvider(props) {
    const [addToCartState, setAddToCartState] = useState()
    const [filtersObj, setFiltersObj] = useState()
    const [ filtersList, setFiltersList ] = useState()
    function changeAddToCart(newState) {
        console.log(newState)
        setAddToCartState(newState)
    }

    const flattenObject = (obj) => {
        const flattened = {}

        Object.keys(obj).forEach((key) => {
            if (typeof obj[key] === 'object' && obj[key] !== null) {
                Object.assign(flattened, flattenObject(obj[key]))
            } else {
                flattened[key] = obj[key]
            }
        })

        return flattened
    }

    function getFiltersList(sampleData) {
        const flattenedObj = flattenObject(sampleData)

    }

    return (
        <FiltersContext.Provider value={{ addToCartState, changeAddToCart, filtersObj, setFiltersObj}}>
            {props.children}
        </FiltersContext.Provider>
    )
}

export {FiltersContextProvider, FiltersContext}
