import React, {createRef, useContext, useEffect, useState} from 'react'
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import {Grid} from "@material-ui/core";
import FiltersComponent from "../Components/FiltersComponent";
import ProductListComponent from "../Components/ProductListComponent";
import {FiltersContext} from "../FiltersContext";

const HomePage = () => {
    // const [ loading, setLoading ] = useState(false)
    const [ data2, setData ] = useState()
    const { filtersObj, setFiltersObj, loading, setLoading } = useContext(FiltersContext)

    useEffect(() => fetchData(), []);

    const flattenObject = (obj) => {
        const flattened = {}

        Object.keys(obj).forEach((key) => {
            if (typeof obj[key] === 'object' && obj[key] !== null) {
                Object.assign(flattened, flattenObject(obj[key]))
            } else {
                flattened[key] = obj[key]
            }
        })

        return flattened
    }

    const getFilterOptions = (data, keyName) => {
        const options = []

        data.forEach(dt => {
            const flattenedDt = flattenObject(dt)
            options.push(flattenedDt[keyName].toString())
        })

        return [...new Set(options)]
    }

    const setFiltersObject = (data) => {
        const allFields = flattenObject(data[0])
        let filterObjects = []

        for (const field in allFields) {
            let object = {
                name: field,
                options: getFilterOptions(data, field),
                ref: createRef(),
                changeOption: changeFilterOption
            }
            filterObjects.push(object)
        }

        setFiltersObj(filterObjects)
    }

    const changeFilterOption = (currentI, newOption = "AAA") => {
        // if (filtersObj !== undefined && filtersObj[currentI] !== undefined) {
        //     filtersObj[currentI].selectedOption = newOption
            console.log(filtersObj)
        //     setFiltersObj(filtersObj)
        // }
    }

    const fetchData = () => {
        setLoading(true)

        // fetch("../mock.txt")
        fetch("https://www.homedepot.ca/api/search/v1/search?category=1010108&store=7008&filter=nz0&pageSize=40&lang=en")
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setFiltersObject(data['products'])
                setData(data['products'])
            })
            .then(() => {
                setLoading(false)
            })
    }

    return <div style={{marginTop: '30px'}}>
        <CssBaseline />
        <Container maxWidth="false">
            <Grid container spacing={3}>
                { loading ? <></> :
                    <>
                        <Grid item xs={4}>
                            <FiltersComponent filtersObj={filtersObj} />
                        </Grid>
                        <Grid item xs={8}>
                            <ProductListComponent loading={loading} products={data2} />
                        </Grid>
                    </>
                }
            </Grid>
        </Container>
    </div>
}

export default HomePage
