import React, {useState} from "react"
const FiltersContext = React.createContext()

function FiltersContextProvider(props) {
    // const [addToCartState, setAddToCartState] = useState()
    const [filtersObj, setFiltersObj] = useState()
    const [ filtersList, setFiltersList ] = useState([])

    function changeAddToCart(newState) {
        // console.log(newState)
        // setAddToCartState(newState)
    }

    const updateFiltersList = (field, newOptionsList) => {
        const newFilters = filtersList
        newFilters[field] = newOptionsList
        setFiltersList(newFilters)
    }

    return (
        <FiltersContext.Provider value={{ changeAddToCart, filtersObj, setFiltersObj, filtersList, updateFiltersList}}>
            {props.children}
        </FiltersContext.Provider>
    )
}

export {FiltersContextProvider, FiltersContext}
