import React, {createRef, useContext, useEffect, useState} from 'react'
import CssBaseline from '@material-ui/core/CssBaseline';
import Container from '@material-ui/core/Container';
import {Button, Grid, TextField} from "@material-ui/core";
import FiltersComponent from "../Components/FiltersComponent";
import ProductListComponent from "../Components/ProductListComponent";
import {FiltersContext} from "../Context/FiltersContext";
import {flattenObject} from "../Functions/commonFunctions";

const HomePage = () => {
    const [ data2, setData ] = useState()
    const [ url, setUrl ] = useState("https://www.homedepot.ca/api/search/v1/search?q=wood&store=7008&pageSize=40&lang=en")
    const { filtersObj, setFiltersObj, loading, changeLoading } = useContext(FiltersContext)

    useEffect(() => fetchData(), []);

    const getFilterOptions = (data, keyName) => {
        const options = []

        data.forEach(dt => {
            const flattenedDt = flattenObject(dt)
            if (flattenedDt[keyName] != undefined) {
                options.push(flattenedDt[keyName].toString())
            }
        })

        return [...new Set(options)]
    }

    const setFiltersObject = (data) => {
        const allFields = flattenObject(data[0])
        let filterObjects = []

        for (const field in allFields) {
            let object = {
                name: field,
                options: getFilterOptions(data, field),
                ref: createRef(),
            }
            filterObjects.push(object)
        }

        setFiltersObj(filterObjects)
    }

    const fetchData = () => {
        changeLoading(true)
        console.log("https://dry-scrubland-62237.herokuapp.com/" + url)
        // const options = {
        //     headers: new Headers({'x-requested-with': 'XMLHttpRequest'}),
        // }
        fetch("https://dry-scrubland-62237.herokuapp.com/" + url)
            .then(response => response.json())
            .then(data => {
                console.log(data)
                setFiltersObject(data['products'])
                setData(data['products'])
            })
            .then(() => {
                changeLoading(false)
            })
    }

    return <div style={{marginTop: '30px'}}>
        <CssBaseline />
        <Container maxWidth="false">
            <Grid container spacing={3}>
                <Grid item xs={10}>
                    <TextField
                        id="standard-full-width"
                        label="URL"
                        style={{ margin: 8 }}
                        placeholder="https://"
                        helperText=""
                        fullWidth
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}
                        defaultValue="https://www.homedepot.ca/api/search/v1/search?q=wood&store=7008&pageSize=40&lang=en"
                        onChange={(e) => setUrl(e.target.value)}
                    />
                </Grid>
                <Grid item xs={2} style={{marginTop: 15}}>
                    <Button variant="contained" fullWidth onClick={fetchData}>Query</Button>
                </Grid>
            </Grid>
            <Grid container spacing={3}>
                { loading ? <></> :
                    <>
                        <Grid item xs={4}>
                            <FiltersComponent filtersObj={filtersObj} />
                        </Grid>
                        <Grid item xs={8}>
                            <ProductListComponent loading={loading} products={data2} />
                        </Grid>
                    </>
                }
            </Grid>
        </Container>
    </div>
}

export default HomePage
