import React, {useContext} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TrueFalseSelectionsComponent from "./TrueFalseSelectionsComponent";
import {Grid} from "@material-ui/core";

import { FiltersContext } from "../FiltersContext";
import Select from 'react-select'

const useStyles = makeStyles({
    root: {
        minWidth: 275,
    },
    selectContainer: {
        display: 'flex',
        flexWrap: 'nowrap'
    },
    selection: {
        width: '100%'
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
});




const FiltersComponent = (props) => {
    const classes = useStyles();
    const {addToCartState, changeAddToCart} = useContext(FiltersContext)
    const { filtersObj } = props

    const loadOptionsForSelect = (variants) => {
        const options = []
        variants.forEach(variant => {
            options.push({value: variant, label: variant})
        })
        return options
    }

    const changedFilters = (field, selectedOptions) => {

    }
    const renderFilters = () => {
        if (filtersObj !== undefined) {
            return (
                    filtersObj.map((field, index) => (
                        <Grid key={index} item xs={12} md={6}>
                            <br />{field.name}
                            <Select
                                placeholder={field.name}
                                closeMenuOnSelect={false}
                                name={field.name}
                                options={loadOptionsForSelect(field.options)}
                                isMulti
                                onChange={selectedOptions => { console.log(selectedOptions)} }
                            />

                            {/*<TrueFalseSelectionsComponent*/}
                            {/*    className={classes.selection}*/}
                            {/*    selectionName={field.name}*/}
                            {/*    options={field.options}*/}
                            {/*    selectedOption={field.selectedOption}*/}
                            {/*    changeOption={field.changeOption}*/}
                            {/*    index={index}*/}
                            {/*/>*/}
                        </Grid>
                ))
            )
        }
    }

    return <Card className={classes.root}>
        <CardContent>
            <Typography variant="h5" component="h2">
                Product Query and Filters
            </Typography>
            <Grid container spacing={1} style={{marginTop: '10px'}}>
                {
                    renderFilters()
                }


                <Grid item xs={12} md={4}>
                    <TrueFalseSelectionsComponent
                        className={classes.selection}
                        selectionName="Add to Cart"
                        options={[true.toString(), false.toString()]}
                        selectedOption={addToCartState}
                        changeOption={changeAddToCart}
                    />
                </Grid>

                <Grid item xs={12} md={4}>
                    {/*<TrueFalseSelectionsComponent className={classes.selection} selectionName="bopisAddToCart"/>*/}
                </Grid>
                <Grid item xs={12} md={4}>
                    {/*<TrueFalseSelectionsComponent className={classes.selection} selectionName="hasMoreOptions"/>*/}
                </Grid>
            </Grid>
        </CardContent>
        <CardActions>
            <Button size="small">Reset All</Button>
        </CardActions>
    </Card>
}

const getUniqueData = (productFieldData) => {
    return [...new Set(productFieldData)]
}

export default FiltersComponent
