import React, {useContext, useEffect, useState} from 'react'
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";

import { FiltersContext } from "../FiltersContext";
import {Button, Snackbar} from "@material-ui/core";
import {Alert} from "@material-ui/lab";

const useRowStyles = makeStyles({
    root: {
        "& > *": {
            borderBottom: "unset"
        }
    }
});

const flattenObject = (obj) => {
    const flattened = {}
    Object.keys(obj).forEach((key) => {
        if (typeof obj[key] === 'object' && obj[key] !== null) {
            Object.assign(flattened, flattenObject(obj[key]))
        } else {
            flattened[key] = obj[key]
        }
    })

    return flattened
}

function createData(flatProduct) {

    let f1 = flatProduct['name'].toString()
    let f2 = flatProduct['productId'].toString()
    let f3 = flatProduct['addToCart'].toString()
    let f4 = flatProduct['bopisAddToCart'].toString()
    let f5 = flatProduct['formattedValue'].toString()
    let f6 = flatProduct['productStatus'].toString()
    let f7 = flatProduct['stockLevelStatus'].toString()

    return {
        f1: f1,
        f2: f2,
        f3: f3,
        f4: f4,
        f5: f5,
        f6: f6,
        f7: f7,
        f8: f7,
        // history: [
        //     { date: "2020-01-05", customerId: "11091700", amount: 3 },
        //     { date: "2020-01-02", customerId: "Anonymous", amount: 1 }
        // ]
    }
}

function Row(props) {
    const { row } = props;
    const [open, setOpen] = React.useState(false);
    const classes = useRowStyles();

    return (
        <React.Fragment>
            <TableRow className={classes.root}
                      // onClick={() => setOpen(!open)}
            >
                <TableCell>
                    <IconButton
                        aria-label="expand row"
                        size="small"
                        onClick={() => setOpen(!open)}
                    >
                        {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    </IconButton>
                </TableCell>

                <TableCell component="th" scope="row"> {row.f1}</TableCell>
                <TableCell align="right"
                           onClick={() => {
                               navigator.clipboard.writeText(row.f2)
                               setOpen(true)
                           }}

                ><Button href="#">{row.f2}</Button></TableCell>

                <TableCell align="right">{row.f3}</TableCell>
                <TableCell align="right">{row.f4}</TableCell>
                <TableCell align="right">{row.f5}</TableCell>
                <TableCell align="right">{row.f6}</TableCell>
                <TableCell align="right">{row.f7}</TableCell>

                {/*<Snackbar open={open} autoHideDuration={6000}>*/}
                {/*    <Alert severity="success">*/}
                {/*        Product ID Copied To Clipboard*/}
                {/*    </Alert>*/}
                {/*</Snackbar>*/}

            </TableRow>
            {/*<TableRow>*/}
            {/*    <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>*/}
            {/*        <Collapse in={open} timeout="auto" unmountOnExit>*/}
            {/*            <Box margin={1}>*/}
            {/*                <Typography variant="h6" gutterBottom component="div">*/}
            {/*                    History*/}
            {/*                </Typography>*/}
            {/*                <Table size="small" aria-label="purchases">*/}
            {/*                    <TableHead>*/}
            {/*                        <TableRow>*/}
            {/*                            <TableCell>Date</TableCell>*/}
            {/*                            <TableCell>Customer</TableCell>*/}
            {/*                            <TableCell align="right">Amount</TableCell>*/}
            {/*                            <TableCell align="right">Total price ($)</TableCell>*/}
            {/*                        </TableRow>*/}
            {/*                    </TableHead>*/}
            {/*                    <TableBody>*/}
            {/*                        /!*{row.history.map((historyRow) => (*!/*/}
            {/*                        /!*    <TableRow key={historyRow.date}>*!/*/}
            {/*                        /!*        <TableCell component="th" scope="row">*!/*/}
            {/*                        /!*            {historyRow.date}*!/*/}
            {/*                        /!*        </TableCell>*!/*/}
            {/*                        /!*        <TableCell>{historyRow.customerId}</TableCell>*!/*/}
            {/*                        /!*        <TableCell align="right">{historyRow.amount}</TableCell>*!/*/}
            {/*                        /!*        <TableCell align="right">*!/*/}
            {/*                        /!*            {Math.round(historyRow.amount * row.price * 100) / 100}*!/*/}
            {/*                        /!*        </TableCell>*!/*/}
            {/*                        /!*    </TableRow>*!/*/}
            {/*                        /!*))}*!/*/}
            {/*                    </TableBody>*/}
            {/*                </Table>*/}
            {/*            </Box>*/}
            {/*        </Collapse>*/}
            {/*    </TableCell>*/}
            {/*</TableRow>*/}
        </React.Fragment>
    );
}

Row.propTypes = {
    row: PropTypes.shape({
        history: PropTypes.arrayOf(
            PropTypes.shape({
                amount: PropTypes.number.isRequired,
                customerId: PropTypes.string.isRequired,
                date: PropTypes.string.isRequired
            })
        ).isRequired,
        // name: PropTypes.string.isRequired,
        // productId: PropTypes.number.isRequired,
    }).isRequired
};

const ComponentA = ({products2, filtersList}) => {
    // const [ rows22, setRows22 ] = useState([])
    // console.log(products2)
    // const { rows, setRows } = useState([])
    let rows22 = []

    // loop over all products:
    // loop over all filter:
    // loop over all conditions inside filter:
    // need to find where inside filter = productFilter
    // add by default, but if any one condition is nonmatch, then exit.



    if (products2 !== undefined ) {
        const products = Object.entries(products2)
        if (products != undefined) {

            products.forEach(product => {
                const flatProduct = flattenObject(product)

                let addProduct = false
                // console.log(filtersList)

                let matchedFilters = 0
                let requiredFiltersMatch = 0

                for (const filter of Object.keys(filtersList)) {
                    // ole.log(filtersList[filter.toString()])
                    // console.log("A ->" + flatProduct[filter])
                    // console.log("B->" + filtersList[filter.toString()][0]["value"])
                    // console.log("B->" + filter)

                    if (flatProduct[filter] != undefined && filtersList[filter.toString()] != undefined && filtersList[filter.toString()].length > 0) {
                        filtersList[filter.toString()].forEach(f => {
                            if (f["value"].toString() == flatProduct[filter].toString()) {
                                console.log("A->" + f["value"].toString())
                                console.log("B->" + flatProduct[filter].toString())
                                matchedFilters++
                            }
                        })
                    }
                    console.log(filtersList[filter.toString()].isEmpty())
                    requiredFiltersMatch++
                }
                console.log(matchedFilters + " ===" + requiredFiltersMatch)
                if (matchedFilters === requiredFiltersMatch) {
                    // setRows22([...rows22, flatProduct])
                    rows22.push(createData(flatProduct))
                }
            })
        }
    }


    // console.log(rows22)

    if (rows22.length > 0) {
        return (
            rows22.map((row) => (
                <Row key={row.f2} row={row} />
            ))
        )
    } else {
        return (
            <>
                <TableCell>No Products Found</TableCell>
            </>
        )
    }
}


const ProductListComponent = props => {
    const { filtersList2 }  = useContext(FiltersContext)



    if (props.loading) {
        return "Loading..."
    } else {
        return (
            <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                    <TableHead>
                        <TableRow>
                            <TableCell />
                            <TableCell>name</TableCell>
                            <TableCell align="right">productId</TableCell>
                            <TableCell align="right">addToCart</TableCell>
                            <TableCell align="right">bopisAddToCart</TableCell>
                            <TableCell align="right">displayPriceFormatted</TableCell>
                            <TableCell align="right">productStatus</TableCell>
                            <TableCell align="right">stockLevelStatus</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        <ComponentA products2={props.products} filtersList={filtersList2}/>
                    </TableBody>
                </Table>
            </TableContainer>
        );
    }
}

export default ProductListComponent
