import React, {useContext, useEffect} from 'react'
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";

import { FiltersContext } from "../FiltersContext";

const useRowStyles = makeStyles({
    root: {
        "& > *": {
            borderBottom: "unset"
        }
    }
});

function createData(name, productId, addToCart, bopisAddToCart, displayPriceFormatted, productStatus, stockLevelStatus) {
    return {
        name,
        productId,
        addToCart,
        bopisAddToCart,
        displayPriceFormatted,
        productStatus,
        stockLevelStatus,
        history: [
            { date: "2020-01-05", customerId: "11091700", amount: 3 },
            { date: "2020-01-02", customerId: "Anonymous", amount: 1 }
        ]
    };
}

function Row(props) {
    const { row } = props;
    const [open, setOpen] = React.useState(false);
    const classes = useRowStyles();

    return (
        <React.Fragment>
            <TableRow className={classes.root} onClick={() => setOpen(!open)}>
                <TableCell>
                    <IconButton
                        aria-label="expand row"
                        size="small"
                        onClick={() => setOpen(!open)}
                    >
                        {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    </IconButton>
                </TableCell>

                <TableCell component="th" scope="row"> {row.name}</TableCell>
                <TableCell align="right">{row.productId}</TableCell>
                <TableCell align="right">{row.addToCart}</TableCell>
                <TableCell align="right">{row.bopisAddToCart}</TableCell>
                <TableCell align="right">{row.displayPriceFormatted}</TableCell>
                <TableCell align="right">{row.productStatus}</TableCell>
                <TableCell align="right">{row.stockLevelStatus}</TableCell>

            </TableRow>
            <TableRow>
                <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
                    <Collapse in={open} timeout="auto" unmountOnExit>
                        <Box margin={1}>
                            <Typography variant="h6" gutterBottom component="div">
                                History
                            </Typography>
                            <Table size="small" aria-label="purchases">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Date</TableCell>
                                        <TableCell>Customer</TableCell>
                                        <TableCell align="right">Amount</TableCell>
                                        <TableCell align="right">Total price ($)</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {row.history.map((historyRow) => (
                                        <TableRow key={historyRow.date}>
                                            <TableCell component="th" scope="row">
                                                {historyRow.date}
                                            </TableCell>
                                            <TableCell>{historyRow.customerId}</TableCell>
                                            <TableCell align="right">{historyRow.amount}</TableCell>
                                            <TableCell align="right">
                                                {Math.round(historyRow.amount * row.price * 100) / 100}
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </Box>
                    </Collapse>
                </TableCell>
            </TableRow>
        </React.Fragment>
    );
}

Row.propTypes = {
    row: PropTypes.shape({
        history: PropTypes.arrayOf(
            PropTypes.shape({
                amount: PropTypes.number.isRequired,
                customerId: PropTypes.string.isRequired,
                date: PropTypes.string.isRequired
            })
        ).isRequired,
        // name: PropTypes.string.isRequired,
        // productId: PropTypes.number.isRequired,
    }).isRequired
};

const data = [
    { name: "A", productId: 9292, addToCart: false, bopisAddToCart: false, displayPriceFormatted: "$234.29", productStatus: "outOfStock", stockLevelStatus: "Out of Stock"},
    // { productId: 192925, name: "ABCA Product"},
    // { productId: 192923, name: "aABCA Product"},
    // { productId: 192924, name: "sABCA Product"},
    // { productId: 192926, name: "dABCA Product"},
    // { productId: 192928, name: "wABCA Product"}
];

const ProductListComponent = props => {
    const { addToCartState } = useContext(FiltersContext)
    console.log(props.products)

    props.products.forEach(p => {
        if (p['fulfillmentOptions']['addToCart'] === addToCartState) {
            rows.push(createData(
                p['code'], p['code'], p['fulfillmentOptions']['addToCart'].toString(), p['fulfillmentOptions']['bopisAddToCart'].toString(), p['pricing']['displayPrice']['formattedValue'],
                p['']
            ))
        }
    })

    const rows = [
        createData("A", "222", false.toString(), false.toString(),"$234.29","outOfStock", "Out of Stock")
    ]

    return (
        <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
                <TableHead>
                    <TableRow>
                        <TableCell />
                        <TableCell>name</TableCell>
                        <TableCell align="right">productId</TableCell>
                        <TableCell align="right">addToCart</TableCell>
                        <TableCell align="right">bopisAddToCart</TableCell>
                        <TableCell align="right">displayPriceFormatted</TableCell>
                        <TableCell align="right">productStatus</TableCell>
                        <TableCell align="right">stockLevelStatus</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row) => (
                        <Row key={row.name} row={row} />
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}

export default ProductListComponent
