import React, {useContext, useEffect, useState} from 'react'
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import {Grid} from "@material-ui/core";
import FiltersComponent from "../Components/FiltersComponent";
import ProductListComponent from "../Components/ProductListComponent";
import {FiltersContext} from "../FiltersContext";

const HomePage = () => {
    const [ loading, setLoading ] = useState(false)
    const [ data, setData ] = useState()
    const { filtersObj, updateFilters} = useContext(FiltersContext)

    useEffect(() => fetchData(), []);

    const fetchData = () => {
        setLoading(true)

        fetch("https://www.homedepot.ca/api/search/v1/search?category=l4-buffets-sideboards&store=7008&filter=&pageSize=40&lang=en")
            .then(response => response.json())
            .then(data => {
                setData(data['products'])
                setFiltersObject()
            })
            // .then(() => setLoading(false))
    }

    return <div style={{marginTop: '30px'}}>
        <CssBaseline />
        <Container maxWidth="false">
            <Grid container spacing={3}>
                <Grid item xs={4}>
                    <FiltersComponent filtersObj={filtersObj} fields={Object.keys(data)} products={data} />
                </Grid>
                <Grid item xs={8}>
                    <ProductListComponent loading={loading} products={data} />
                </Grid>
            </Grid>
        </Container>
    </div>

    const setFiltersObject = () => {
        let filtersObject = {}

        filtersObject = [
            {
                fieldName: "Name",
                options: ["Option 1", "Option 2"],
                selectedOption: "ABC",
                changeOption: changeOption
            }
        ]

        updateFilters(filtersObject)
    }

    const changeOption = (currentI, newOption) => {
        filtersObj[currentI].selectedOption = newOption
        updateFilters(filtersObj)
    }
}

export default HomePage
